const App = () => {
  const [count, setCount] = useState(0);
  const handleClick = () => {
    setCount(count + 1);
  };
  const buttonText = count === 0 ? 'Başla' : count;

  return (
    <div>
      <h1>Injection Örneği</h1>
      <Button onClick={handleClick} text={buttonText} />
    </div>
  );
};

